def get_joke():
    return random.choice(jokes_list)

def get_all_jokes():
    return jokes_list

def show_window():
    joke = get_joke()
    root = tk.Tk()
    root.title("Marathi Joke ЁЯШЕ")
    root.geometry("400x200")

    label = tk.Label(root, text=joke, font=("Nirmala UI", 12), wraplength=350, justify="center")
    label.pack(pady=20, padx=10)

    button = tk.Button(root, text="ЁЯШВ рдЖрдгрдЦреА рдПрдХ", command=lambda:[root.destroy(), show_window()])
    button.pack(pady=10)

    root.mainloop()
