record.field:
user = []
if rec.picking_type_id.warehouse_id:
    if rec.picking_type_id.warehouse_id.manager_id:
        user.append(rec.picking_type_id.warehouse_id.manager_id.id)

    if rec.picking_type_id.warehouse_id.supervisor_ids:
        user += rec.picking_type_id.warehouse_id.supervisor_ids.ids
else:
    if rec.picking_type_category_id.manager_id:
        user.append(rec.picking_type_category_id.manager_id.id)

    if rec.picking_type_category_id.supervisor_ids:
        user += rec.picking_type_category_id.supervisor_ids.ids
